import React, { useState } from 'react';
import Draggable from 'react-draggable';

const ReviewSlider = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const images = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',

  ];

  const handleDrag = (e, ui) => {
    const tab = document.querySelector("tabs-box");
    tab.scrollLeft -= e.movementX
    // You can update the activeIndex based on drag position or other logic
    // Here, we are just updating it based on whether it is dragged to the left or right
    if (ui.deltaX > 0 && activeIndex < images.length - 1) {
      setActiveIndex(activeIndex + 1);

    } else if (ui.deltaX < 0 && activeIndex > 0) {
      setActiveIndex(activeIndex - 1);
    }
    
  };

  return (
    <div className='tabs-box bg-white'>
      <h2>Draggable Photo Slider</h2>
      <Draggable
        axis="x"
        bounds={{ left: -100, right: 100 }}
        onStop={() => setActiveIndex(0)} // Reset the position when dragging stops
      >
        <div
          style={{
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            height: '300px',
            overflow: 'hidden',
          }}
        >
          {images.map((image, index) => (
            <div
              key={index}
              style={{
                width: '100%',
                transform: `translateX(${
                  -activeIndex * 100 + index * 100
                }%)`,
                transition: 'transform 0.3s ease',
              }}
            >
              1
              <img
                src={image}
                alt={`slide ${index + 1}`}
                style={{ width: '100%', height: '100%', objectFit: 'cover' }}
              />
            </div>
          ))}
        </div>
      </Draggable>
    </div>
  );
};

export default ReviewSlider;
